<script setup>
  import { onMounted } from 'vue'
  
  onMounted(() => {
    // Delay base per iniziare l'effetto onda
    setTimeout(() => {
      animateDisplacement('displace-title')
      animateDisplacement('displace-credit')
      animateDisplacement('displace-stella')
    }, 3000)
  })
  
  function animateDisplacement(id) {
    const el = document.getElementById(id)
    let scale = 0
    const interval = setInterval(() => {
      scale += 0.5
      el.setAttribute('scale', scale.toString())
      if (scale >= 20) clearInterval(interval)
    }, 50)
  }
</script>
  
<style scoped>
  .logo-container {
    width: 100vw;
    height: 100vh;
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    gap: 1.5rem;
    overflow: hidden;
  }
  
  .title,
  .credit {
    opacity: 0;
    transform: scale(0.95);
    animation: appear 2s ease forwards;
    animation-delay: 0.8s;
  }
  
  .credit {
    animation-delay: 1.4s;
  }
  
  .stella {
    transform: translateY(20px);
    opacity: 0;
    animation: floatIn 2s ease forwards;
    animation-delay: 0.4s;
  }
  
  @keyframes appear {
    to {
      opacity: 1;
      transform: scale(1);
    }
  }
  
  @keyframes floatIn {
    to {
      opacity: 1;
      transform: translateY(0);
    }
  }
</style>
  